import { useState, useEffect } from "react";
import "../App.css";

import { Story } from "./Story";

import { getStoryWithID } from "../APICalls/getApiStories";
import { storyComments } from "../APICalls/apiEndPoints";

export const StoryTemplate = ({ storyId, searchTerm }) => {
  const [story, setStory] = useState([]);
  const [domain, setDomain] = useState([]);

  useEffect(() => {
    getStoryWithID(storyId).then((storyd) => {
      setStory(storyd);

      // parse the domain so it looks better
      if (storyd && storyd.url != null) {
        let domain = new URL(storyd.url);
        domain = domain.hostname.replace("www.", "");
        setDomain(domain);
      }
    });
  }, []);

  if (searchTerm == "") {
    return <Story story={story} />;
  } else if (story.title.toLowerCase().includes(searchTerm.toLowerCase())) {
    return <Story story={story} />;
  }

  return (
    <div className="news-card">
      {story ? (
        <>
          <div className="news-card-counter">
            <p> 1. </p>
          </div>
          <div className="news-card-body">
            <div className="card-title">
              {}
              <a
                className="title"
                href={domain ? story.url : storyComments + story.id}
              >
                {story.title}
              </a>
              <span className="title-url">
                {" "}
                {domain ? "(" + domain + ")" : null}
              </span>
            </div>
            <div className="card-stats">
              <span className="stats">
                {story.score} points by{" "}
                <a
                  className="stats"
                  href={`https://news.ycombinator.com/user?id=${story.by}`}
                >
                  {story.by}
                </a>{" "}
                {story.time} | hide |{" "}
                <a className="stats" href={storyComments + story.id}>
                  {story.descendants} comments{" "}
                </a>
              </span>
              <span></span>
            </div>
          </div>
        </>
      ) : (
        <p>Loading</p>
      )}
    </div>
  );
};
