import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../App.css";
import { getStoryWithID } from "../APICalls/getApiStories";

export const StoryTemplate = ({ storyId }) => {
  const [story, setStory] = useState([]);

  useEffect(() => {
    getStoryWithID(storyId).then((storyd) => {
      setStory(storyd);
    });
  }, []);

  return (
    <div className="news-card">
      {story ? (
        <>
          <div className="news-card-counter">
            <p> 1. </p>
          </div>
          <div className="news-card-body">
            <div className="card-title">
              <a href={story.url}>
                <span className="title">{story.title}</span>
              </a>
              <span className="title-url"> (ingebrigtsen.no) </span>
            </div>
            <div className="card-stats">
              <span className="stats">
                {story.score} points by {story.by} 1 hour ago | hide |{" "}
                {story.descendants} comments
              </span>
              <span></span>
            </div>
          </div>
        </>
      ) : (
        <p>Loading</p>
      )}
    </div>
  );
};
